package Ex389FindtheDifference;

import java.util.HashMap;

/**
 * Given two strings s and t which consist of only lowercase letters.
 * <p>
 * String t is generated by random shuffling string s and then add one more letter at a random position.
 * <p>
 * Find the letter that was added in t.
 * <p>
 * Example:
 * <p>
 * Input:
 * s = "abcd"
 * t = "abcde"
 * <p>
 * Output:
 * e
 * <p>
 * Explanation:
 * 'e' is the letter that was added.
 */
public class Solution {
    public char findTheDifference(String s, String t) {
        char key = t.charAt(0);
        for (int i = 0; i < t.length(); i++) {
            key = t.charAt(i);
            if (s.indexOf(key) == -1) return key;
            else {
                int position = s.indexOf(key);
                String before = s.substring(0, position);
                String after = s.substring(position+1, s.length());
                s = before.concat(after);
            }
        }
        return key;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.findTheDifference("", ""));
    }
}
